// import './App.css';
import { useState, useEffect } from "react";
const cheerio = require("cheerio");
const axios = require("axios");

const array = [];

const Home = () => {
const [wordsData, setWordsData] = useState([]);
useEffect(() => {
axios.get("http://shakespeare.mit.edu/macbeth/full.html").then((res) => {
  const $ = cheerio.load(res.data);
  const text = $("html").text();
  const words = text
    .toLowerCase()
    .replace(/[^a-zA-Z ]/g, " ")
    .replace(/\s\s+/g, " ")
    .split(" ");

  words.forEach((currentWord) => {
    if (array.some((object) => object.word === currentWord)) {
      const findObject = array.find((object) => object.word === currentWord);
      findObject.count += 1;
    } else {
      array.push({ word: currentWord, count: 1 });
    }
  });
  const sortedArray = array.sort((a, b) => (a.count < b.count ? 1 : -1));
  // console.dir(sortedArray, {'maxArrayLength': null})
  // console.log(sortedArray);
  setWordsData(words)
});
}, [])
return [wordsData]
}
