// import './App.css';
import { useState, useEffect } from 'react'
const cheerio = require("cheerio");
const axios = require("axios");

// https://cors-anywhere.herokuapp.com/

const App = () => {
  // let array = [];
  // let tempArray = []
  const [array, setArray] = useState([]);
  useEffect(() => {
  const getData = async () => {
  await axios
    .get(
      "https://secret-ocean-49799.herokuapp.com/http://shakespeare.mit.edu/macbeth/full.html"
    )
    .then((res) => {
      const $ = cheerio.load(res.data);
      const text = $("html").text();
      const words = text
        .toLowerCase()
        .replace(/[^a-zA-Z ]/g, " ")
        .replace(/\s\s+/g, " ")
        .split(" ");

      words.forEach((currentWord) => {
        if (array.some((object) => object.word === currentWord)) {
          const findObject = array.find(
            (object) => object.word === currentWord
          );
          findObject.count += 1;
        } else {
          array.push({ word: currentWord, count: 1 });
        }
      });
      // array = array.sort((a, b) => (a.count < b.count ? 1 : -1));
      setArray(array.sort((a, b) => (a.count < b.count ? 1 : -1)).slice(0, 20))
      // console.log(array.slice(0, 20));
    })
  }
  getData()
    }, []);
  return (
    <table style={{width: "100%"}}>
      <h2>Word Frequency Table</h2> 
      <tr>
      <th style={{width: "50%"}}>Word</th>
      <th style={{width: "50%"}}>Word Frequency</th>
      </tr>
      {array.map(object => (
        <tr>
        <td>{object.word}</td>
        <td>{object.count}</td>
        </tr>
        )
      )}
    </table>
  );
};

export default App;
