// import './App.css';
const cheerio = require("cheerio");
const axios = require("axios");

let array = [];
// https://cors-anywhere.herokuapp.com/

const App = () => {
  let headers = new Headers();

    headers.append('Content-Type', 'application/json');
    headers.append('Accept', 'application/json');
    // headers.append('Authorization', 'Basic ' + base64.encode(username + ":" +  password));
    headers.append('Origin','http://localhost:3001');

axios.get("http://shakespeare.mit.edu/macbeth/full.html", 
{
  mode: 'cors',
  credentials: 'include',
  method: 'GET',
  headers: headers
}).then((res) => {
  const $ = cheerio.load(res.data);
  const text = $("html").text();
  const words = text
    .toLowerCase()
    .replace(/[^a-zA-Z ]/g, " ")
    .replace(/\s\s+/g, " ")
    .split(" ");

  words.forEach((currentWord) => {
    if (array.some((object) => object.word === currentWord)) {
      const findObject = array.find((object) => object.word === currentWord);
      findObject.count += 1;
    } else {
      array.push({ word: currentWord, count: 1 });
    }
  });
  array = array.sort((a, b) => (a.count < b.count ? 1 : -1));
  // console.dir(sortedArray, {'maxArrayLength': null})
  // console.log(sortedArray);
});

return (
  <div>

  </div>
)
}

export default App;
