// import './App.css';
import { useState, useEffect } from 'react'
const cheerio = require("cheerio");
const axios = require("axios");

// https://cors-anywhere.herokuapp.com/

// let array = [];
const App = () => {
  const [array, setArray] = useState([]);
  useEffect(() => { 
  axios
    .get(
      "https://secret-ocean-49799.herokuapp.com/http://shakespeare.mit.edu/macbeth/full.html"
    )
    .then((res) => {
      const $ = cheerio.load(res.data);
      const text = $("html").text();
      const words = text
        .toLowerCase()
        .replace(/[^a-zA-Z ]/g, " ")
        .replace(/\s\s+/g, " ")
        .split(" ");

      words.forEach((currentWord) => {
        if (array.some((object) => object.word === currentWord)) {
          const findObject = array.find(
            (object) => object.word === currentWord
          );
          findObject.count += 1;
        } else {
          array.push({ word: currentWord, count: 1 });
        }
      });
      setArray(array.sort((a, b) => (a.count < b.count ? 1 : -1)));
      // console.dir(sortedArray, {'maxArrayLength': null})
      // console.log(sortedArray);
    })
    }, [array]);

  console.log(array);
  return (
    <div>
      <h2>Asafe</h2>
      {array.map(object => {
        // for (let i = 0; i < 20; i++) {
        console.log(array);
        return <div>{object.word}</div>;
        // }
      })}
      <h4>Cachaca</h4>
    </div>
  );
};

export default App;
